import { useState, useContext } from 'react';
import '../App.scss';
import logo from '../images/logo.png';
import Fab from '@mui/material/Fab';
import { useHistory, Redirect } from "react-router-dom";
import { toast } from "react-toastify";




// Design
import {
  TextField,
  InputAdornment,
  IconButton,
  OutlinedInput,
  FormControl,
  InputLabel,
} from '@mui/material';
import VisibilityIcon from '@mui/icons-material/Visibility';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';
// functions
import { login } from "../api/user";



const Login = () => {
  const history = useHistory();


  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  return (
    <div className="login_form_style container col-10 col-sm-8 col-md-6 col-lg-3">
      <img className="logo" src={logo} alt="logo cod Challenge" />

      <div className="text-center mb-5 alert alert-success">
        <label htmlFor="" className="h2">
          Login
        </label>
      </div>

      <div className="form-group">
        <TextField
          size="small"
          variant="outlined"
          className="form-control"
          label="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
      </div>

      <div className="form-group">
        <FormControl
          variant="outlined"
          size="small"
          className="form-control mt-2"
        >
          <InputLabel>Password</InputLabel>

          <OutlinedInput
            label="Password"
            type={showPassword ? 'text' : 'password'}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            endAdornment={
              <InputAdornment>
                <IconButton
                  edge="end"
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? <VisibilityIcon /> : <VisibilityOffIcon />}
                </IconButton>
              </InputAdornment>
            }
          />
        </FormControl>
      </div>

      <div className="text-center mt-4">
        {/*  <Button variant="contained" disabled={!email || !password}>
          Submit
        </Button> */}

        <Fab className="w-50" variant="extended" disabled={!email || !password}>
          Submit
        </Fab>
      </div>
    </div>
  );
};

export default Login;
